import React, { useState, useEffect } from 'react';

const API_BASE = '/texon-invoicing-portal/api';

function InvoiceDashboard({ token }) {
  const [statistics, setStatistics] = useState(null);
  const [unpaidInvoices, setUnpaidInvoices] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [dateRange, setDateRange] = useState({
    start: '2024-01-01',
    end: new Date().toISOString().split('T')[0]
  });

  // Load invoice statistics
  const loadStatistics = async () => {
    try {
      const response = await fetch(
        `${API_BASE}/invoices/statistics?start_date=${dateRange.start}&end_date=${dateRange.end}`,
        { headers: { 'Authorization': `Bearer ${token}` } }
      );

      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          setStatistics(data.statistics);
        }
      }
    } catch (error) {
      console.error('Error loading statistics:', error);
    }
  };

  // Load unpaid invoices
  const loadUnpaidInvoices = async () => {
    setIsLoading(true);
    setError('');

    try {
      const response = await fetch(
        `${API_BASE}/invoices/unpaid?start_date=${dateRange.start}&end_date=${dateRange.end}&limit=10`,
        { headers: { 'Authorization': `Bearer ${token}` } }
      );

      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          setUnpaidInvoices(data.invoices);
        } else {
          setError(data.error || 'Failed to load invoices');
        }
      } else {
        setError('Server error loading invoices');
      }
    } catch (error) {
      setError('Network error loading invoices');
    }

    setIsLoading(false);
  };

  // Load data when component mounts or date range changes
  useEffect(() => {
    loadStatistics();
    loadUnpaidInvoices();
  }, [dateRange]);

  const handleDateRangeChange = (field, value) => {
    setDateRange(prev => ({ ...prev, [field]: value }));
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount || 0);
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  return (
    <div className="dashboard">
      <h2>Invoice Dashboard</h2>

      {/* Date Range Filter */}
      <div className="date-filter">
        <h3>ðŸ“… Date Range Filter</h3>
        <div className="date-inputs">
          <div>
            <label>Start Date:</label>
            <input
              type="date"
              value={dateRange.start}
              onChange={(e) => handleDateRangeChange('start', e.target.value)}
            />
          </div>
          <div>
            <label>End Date:</label>
            <input
              type="date"
              value={dateRange.end}
              onChange={(e) => handleDateRangeChange('end', e.target.value)}
            />
          </div>
        </div>
      </div>

      {/* Financial Overview */}
      {statistics && (
        <div className="financial-overview">
          <h3>ðŸ’° Financial Overview</h3>
          <div className="stats-grid">
            <div className="stat-card total-orders">
              <h4>Total Orders</h4>
              <p className="stat-number">{statistics.total_orders.toLocaleString()}</p>
              <small>All orders in date range</small>
            </div>

            <div className="stat-card paid-orders">
              <h4>Paid Orders</h4>
              <p className="stat-number">{statistics.paid_orders.toLocaleString()}</p>
              <p className="stat-amount">{formatCurrency(statistics.paid_amount)}</p>
            </div>

            <div className="stat-card unpaid-orders">
              <h4>ðŸš¨ Unpaid Orders</h4>
              <p className="stat-number unpaid">{statistics.unpaid_orders.toLocaleString()}</p>
              <p className="stat-amount unpaid">{formatCurrency(statistics.unpaid_amount)}</p>
            </div>

            <div className="stat-card total-revenue">
              <h4>Total Revenue</h4>
              <p className="stat-amount">{formatCurrency(statistics.total_amount)}</p>
              <small>Paid + Outstanding</small>
            </div>
          </div>
        </div>
      )}

      {/* Recent Unpaid Invoices */}
      <div className="unpaid-invoices-section">
        <h3>ðŸ“‹ Recent Unpaid Invoices</h3>

        {error && (
          <div className="error-message">
            <strong>Error:</strong> {error}
          </div>
        )}

        {isLoading ? (
          <div className="loading">ðŸ”„ Loading invoices...</div>
        ) : (
          <>
            {unpaidInvoices.length === 0 ? (
              <div className="no-invoices">
                <p>âœ… No unpaid invoices found in the selected date range.</p>
              </div>
            ) : (
              <div className="invoices-table">
                <table>
                  <thead>
                    <tr>
                      <th>Order #</th>
                      <th>Customer</th>
                      <th>Company</th>
                      <th>Order Date</th>
                      <th>Amount</th>
                      <th>Status</th>
                      <th>Days Outstanding</th>
                    </tr>
                  </thead>
                  <tbody>
                    {unpaidInvoices.map((invoice) => {
                      const orderDate = new Date(invoice.orderDate);
                      const daysOutstanding = Math.floor((new Date() - orderDate) / (1000 * 60 * 60 * 24));

                      return (
                        <tr key={invoice.id} className={daysOutstanding > 30 ? 'overdue' : ''}>
                          <td>
                            <strong>{invoice.orderNumber || `#${invoice.id}`}</strong>
                          </td>
                          <td>
                            <div>
                              <strong>{invoice.customer.name}</strong>
                              <br />
                              <small>{invoice.customer.email}</small>
                            </div>
                          </td>
                          <td>{invoice.company.name || 'N/A'}</td>
                          <td>{formatDate(invoice.orderDate)}</td>
                          <td>
                            <strong>{formatCurrency(invoice.totalAmount)}</strong>
                          </td>
                          <td>
                            <span className={`status-badge ${invoice.paymentStatus.toLowerCase()}`}>
                              {invoice.paymentStatus}
                            </span>
                          </td>
                          <td>
                            <span className={daysOutstanding > 30 ? 'overdue-days' : 'normal-days'}>
                              {daysOutstanding} days
                            </span>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>

                <div className="table-summary">
                  <p>
                    Showing {unpaidInvoices.length} unpaid invoices. 
                    Total outstanding: <strong>
                      {formatCurrency(unpaidInvoices.reduce((sum, inv) => sum + inv.totalAmount, 0))}
                    </strong>
                  </p>
                </div>
              </div>
            )}
          </>
        )}
      </div>

      {/* Quick Actions */}
      <div className="quick-actions">
        <h3>âš¡ Quick Actions</h3>
        <div className="action-buttons">
          <button 
            onClick={loadUnpaidInvoices}
            disabled={isLoading}
            className="btn-primary"
          >
            {isLoading ? 'ðŸ”„ Refreshing...' : 'ðŸ”„ Refresh Invoices'}
          </button>

          <button 
            onClick={() => {
              setDateRange({
                start: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
                end: new Date().toISOString().split('T')[0]
              });
            }}
            className="btn-secondary"
          >
            ðŸ“… Last 30 Days
          </button>

          <button 
            onClick={() => {
              setDateRange({
                start: new Date(Date.now() - 90 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
                end: new Date().toISOString().split('T')[0]
              });
            }}
            className="btn-secondary"
          >
            ðŸ“… Last 90 Days
          </button>
        </div>
      </div>

      {/* Connection Status */}
      <div className="connection-status">
        <h3>ðŸ”— System Status</h3>
        <div className="status-grid">
          <div className="status-item">
            <span className="status-icon">âœ…</span>
            <div>
              <strong>Database Connection</strong>
              <br />
              <small>Connected to Supabase</small>
            </div>
          </div>
          <div className="status-item">
            <span className="status-icon">âœ…</span>
            <div>
              <strong>Brightpearl Data</strong>
              <br />
              <small>Real-time invoice data</small>
            </div>
          </div>
          <div className="status-item">
            <span className="status-icon">âœ…</span>
            <div>
              <strong>API Service</strong>
              <br />
              <small>All systems operational</small>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default InvoiceDashboard;
